import { Fragment, useEffect } from 'react';
import Cart from './components/Cart/Cart';
import Layout from './components/Layout/Layout';
import Products from './components/Shop/Products';
import { useSelector ,useDispatch} from 'react-redux';
import { uiSliseAction } from './components/store/ui-slice';
import Notification from './components/UI/';

function App() {
  const showCart=useSelector(state=>state.ui.cartIsVisible)
  const cart=useSelector(state=>state.cart)
  const notification=useSelector(state=>state.ui.notification)
  const dispatch=useDispatch()
  useEffect(()=>{
const sendCartData=async()=>{
  dispatch(uiSliseAction.showNotification({
    status:'pending',
    title:'Sending...',
    message:'Sending Cart data!!'
  }))
 const response=await fetch('https://food-72908-default-rtdb.firebaseio.com/cart.json',{method:'PUT',body:JSON.stringify(cart)});

 if(!response.ok){
  throw new Error ('sending data is Failed!! ')
  }
 dispatch(uiSliseAction.showNotification({
  status:'success',
  title:'Success...',
  message:'Sending Cart data Successfully!!'
}))
}
sendCartData().catch(error=>{
  dispatch(uiSliseAction.showNotification({
    status:'error',
    title:'Error...',
    message:'Sending Cart data failed!!'
  })) 
})
  },[cart,dispatch])
  return (
    <Fragment>
      <Notification/>
      <Layout>
    {showCart&&<Cart />}  
      <Products />
    </Layout>
    </Fragment>
  );
}

export default App;
